import React, { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPenToSquare } from "@fortawesome/free-solid-svg-icons";
import { faTrash } from "@fortawesome/free-solid-svg-icons";
import TaskDetails from "./TaskDetails"; // Import the TaskDetails component

export const Todo = ({ task, deleteTodo, editTodo, toggleComplete }) => {
    const [showDetails, setShowDetails] = useState(false); // State variable to track details visibility

    const handleToggleDetails = () => {
        setShowDetails(!showDetails); // Toggle details visibility
    };

    return ( <
        div className = "Todo" >
        <
        p className = { `${task.completed ? "completed" : "incompleted"}` }
        onClick = {
            () => toggleComplete(task.id)
        } > { " " } { task.task } { " " } <
        /p> <
        div >
        <
        FontAwesomeIcon className = "edit-icon"
        icon = { faPenToSquare }
        onClick = {
            () => editTodo(task.id)
        }
        /> <
        FontAwesomeIcon className = "delete-icon"
        icon = { faTrash }
        onClick = {
            () => deleteTodo(task.id)
        }
        /> <
        FontAwesomeIcon className = "details-icon"
        icon = { showDetails ? "fa-minus-circle" : "fa-plus-circle" }
        onClick = { handleToggleDetails }
        />{" "} < /
        div > {
            showDetails && ( <
                TaskDetails task = { task }
                updateTask = {
                    (updatedTask) => editTodo(updatedTask)
                }
                />
            )
        } { " " } <
        /div>
    );
};
